mapper.select:
select E.* from (select rownum as rn, D.post_seq as post_seq, D.board_id as board_id, D.emp_id as emp_id, D.emp_nm as emp_nm, D.dept_id as dept_id, D.dept_name as dept_name, D.jobtitle_id as jobtitle_id, D.jobtitle_nm as jobtitle_nm, lpad(' â†³', (level-1)*4) || D.title as title, D.content as content, D.reg_dt as reg_dt, D.parentpost_id as parentpost_id, D.post_st as post_st from (select A.post_seq as post_seq, A.board_id as board_id, A.emp_id as emp_id, B.emp_nm as emp_nm, B.dept_id as dept_id, F.dept_name as dept_name, B.jobtitle_id as jobtitle_id, C.jobtitle_nm as jobtitle_nm, A.title as title, A.content as content, A.reg_dt as reg_dt, A.parentpost_id as parentpost_id, A.post_st as post_st from busi_post A, emp B, job_title C, dept F
and A.board_id=#{boardId} and A.emp_id = B.emp_id and B.jobtitle_id = C.jobtitle_id and B.dept_id = F.dept_id
)D
and D.${searchCondition} like '%' || #{searchKeyword} || '%'
start with D.parentpost_id is null connect by prior D.post_seq = D.parentpost_id order siblings by D.reg_dt desc, D.post_seq asc )E where E.rn between (#{pageIndex}-1) * #{recordCountPerPage} + 1 and #{pageIndex} * #{recordCountPerPage}

SELECT A.post_seq as post_seq, A.board_id as board_id, A.emp_id as emp_id, B.emp_nm as emp_nm, C.jobtitle_nm as jobtitle_nm, A.title as title, A.content as content, A.reg_dt as reg_dt, A.parentpost_id as parentpost_id, A.post_st as post_st FROM (select * from busi_post
and post_seq = #{postSeq}
)A, emp B, job_title C
and A.emp_id = B.emp_id and B.jobtitle_id = C.jobtitle_id

mapper.insert:
INSERT INTO busi_file ( attachfile_seq, post_seq, filepath, realfilename, attachfile_st ) SELECT busi_attachfile_seq.nextval as attachfileSeq, A.* FROM(
SELECT #{item.postSeq} as postSeq, #{item.filepath} as filepath, #{item.realfilename} as realfilename, #{item.attachfileSt} as attachfileSt FROM dual
) A

mapper.select:
SELECT attachfile_seq, post_seq, filepath, realfilename, attachfile_st FROM busi_file
and post_seq = #{postSeq}
and attachfile_st = 'Y'

SELECT A.reple_seq as reple_seq, A.post_seq as post_seq, A.emp_id as emp_id, B.emp_nm as emp_nm, C.jobtitle_id as jobtitle_id, C.jobtitle_nm as jobtitle_nm, A.reple_cont as reple_cont, A.reg_dt as reg_dt, A.reple_st as reple_st FROM (select * from busi_reple
and post_seq = #{postSeq}
)A, emp B, job_title C
and A.emp_id = B.emp_id and B.jobtitle_id = C.jobtitle_id
ORDER BY reg_dt ASC

SELECT attachfile_seq, post_seq, filepath, realfilename, attachfile_st FROM busi_file
and attachfile_seq = #{attachfileSeq}
and attachfile_st = 'Y'

mapper.update:
UPDATE busi_reple
    SET
        reple_st = #{repleSt}


		and reple_seq = #{repleSeq}

UPDATE busi_post SET
title = #{title},
content=#{content},
post_st = #{postSt},
post_seq = #{postSeq}
and post_seq = #{postSeq}

update.foreach:
UPDATE busi_file
   SET
       attachfile_st = #{item.attachfileSt}

   	and attachfile_seq = #{item.attachfileSeq}
