mapper.select:
SELECT commu_seq, emp_id, commu_nm FROM commu_bkmk
AND emp_id = #{empId}
AND commu_seq in (SELECT commu_seq FROM community WHERE commu_st = 'Y')

SELECT b.* FROM (SELECT rownum as rn, board_seq, commu_seq, emp_id, emp_nm, board_title, board_cont, board_dt, board_st FROM commu_board WHERE commu_seq = #{commuSeq}
and ${searchCondition} like '%' || #{searchKeyword} || '%'
ORDER BY board_seq desc) b WHERE rn between (#{pageIndex}-1) * #{recordCountPerPage} + 1 and #{pageIndex} * #{recordCountPerPage}

SELECT attachfile_seq, board_seq, filepath, realfilename FROM commu_file
AND board_seq = #{boardSeq} AND attachfile_st = 'Y'
AND attachfile_seq = #{attachfileSeq}

mapper.delete:
DELETE FROM commu_bkmk
WHERE commu_seq = #{commuSeq}
AND   emp_id = #{empId}
