mapper.select:
SELECT a. * FROM (SELECT rownum as rn, post_seq, emp_id, emp_nm, title, cont, reg_dt, upd_dt, post_st FROM freeboard
and ${searchCondition} like '%' || #{searchKeyword} || '%'
ORDER BY post_seq DESC) a WHERE rn between (#{pageIndex}-1) * #{recordCountPerPage} + 1 and #{pageIndex} * #{recordCountPerPage}

SELECT attachfile_seq, post_seq, filepath, realfilename FROM free_file
AND post_seq = #{postSeq} AND attachfile_st = 'Y'
AND attachfile_seq = #{attachfileSeq}

mapper.insert:
INSERT INTO freeboard (
			 post_seq
		   , emp_id
		   , emp_nm
		   , title
		   , cont
		   , reg_dt
		   , upd_dt
		   , post_st
)  VALUES (
			 FREE_POST_SEQ.nextval
		   , #{empId}
		   , #{empNm}
		   , #{title}
		   , #{cont}
		   , SYSDATE
		   , SYSDATE
		   , 'Y'

)

mapper.update:
	UPDATE freeboard
	    SET


	        	title = #{title},
		        reg_dt = SYSDATE,
        		post_st = #{postSt}


        		title = #{title},
		        cont = #{cont},
		        reg_dt = SYSDATE


	WHERE emp_id = #{empId}
	AND post_seq = #{postSeq}

UPDATE freeboard
	SET 
		post_st = 'N',
		TITLE = #{title}
WHERE 
		emp_id = #{empId}
	AND post_seq = #{postSeq}
